import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
const API_KEY = 'ce762116';

const MovieList = () => {
  const [movieList, setMovieList] = useState([]);
  const [queryString, setQueryString] = useState('pacino');
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);

  useEffect(() => {
    setIsLoading(true);
    setMovieList([]);
    setErrorMessage(null);
    const abortController = new AbortController();

    const fetchMovies = async () => {
      try {
        const result = await axios.get(
          `http://www.omdbapi.com/?s=${queryString}&apikey=${API_KEY}`,
        );
        console.log({ result });
        if (result.data.Response === 'False') {
          setErrorMessage(result.data.Error);
        } else {
          setMovieList(result.data.Search);
        }
      } catch (error) {
        setErrorMessage(error);
      }
    };
    fetchMovies();
    setIsLoading(false);

    return () => abortController.abort();
  }, [queryString]);

  return (
    <div className="movie-list">
      <div className="movie-list__search input-group">
        <input
          type="text"
          className="form-control"
          placeholder="Search by title or year"
          aria-label="Search by title or year"
        />
      </div>

      <div className="movie-list__cards card-deck">
        {isLoading && (
          <div className="movie-list__loading card">
            <div class="spinner-grow" role="status">
              <span class="sr-only">Loading...</span>
            </div>
          </div>
        )}

        {!isLoading &&
          movieList.length > 0 &&
          movieList.map(movie => (
            <div className="card">
              <img className="card-img-top" src="..." alt="" />
              <div className="card-body">
                <h5 className="card-title">Card title</h5>
                <p className="card-text">
                  This is a wider card with supporting text below as a natural lead-in to additional
                  content. This content is a little bit longer.
                </p>
                <p className="card-text">
                  <small className="text-muted">Last updated 3 mins ago</small>
                </p>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default MovieList;
