import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

import MovieCard from './Card';

const API_KEY = 'ce762116';

const MovieList = () => {
  const [movieList, setMovieList] = useState([]);
  const [queryTitle, setQueryTitle] = useState('satyajit');
  const [queryYear, setQueryYear] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [errorMessage, setErrorMessage] = useState(null);

  useEffect(() => {
    setIsLoading(true);
    setMovieList([]);
    setErrorMessage(null);
    const abortController = new AbortController();

    const fetchMovies = async () => {
      try {
        const result = await axios.get(
          `http://www.omdbapi.com/?s=${queryTitle}&y=${queryYear}&apikey=${API_KEY}`,
        );
        console.log({ result });
        if (result.data.Response === 'False') {
          setErrorMessage(result.data.Error);
        } else {
          setMovieList(result.data.Search);
        }
      } catch (error) {
        setErrorMessage(error);
      }
    };
    fetchMovies();
    setIsLoading(false);

    return () => abortController.abort();
  }, [queryTitle, queryYear]);

  const onMovieCardClick = () => setIsModalOpen(isModalOpen => !isModalOpen);

  return (
    <div className="movie-list">
      <div className="movie-list__search input-group">
        <input
          type="text"
          className="form-control"
          placeholder="Search by Title"
          aria-label="Search by Title"
        />
        <input
          type="text"
          className="form-control"
          placeholder="Search by Year"
          aria-label="Search by Year"
        />
      </div>

      {isLoading && (
        <div className="movie-list__loading">
          <div class="spinner-grow" role="status">
            <span class="sr-only">Loading...</span>
          </div>
        </div>
      )}

      <div className="movie-list__cards row mx-0">
        {!isLoading &&
          movieList.length > 0 &&
          movieList.map(movie => (
            <MovieCard
              key={movie.imdbID}
              title={movie.Title}
              poster={movie.Poster}
              year={movie.Year}
              imdbId={movie.imdbID}
              onMovieCardClick={onMovieCardClick}
            />
          ))}
      </div>
      {isModalOpen && <MovieModal imdbId={imdbId} />}
    </div>
  );
};

export default MovieList;
