import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
const API_KEY = 'ce762116';

const MovieDetails = () => {
  const { id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState(null);
  const [movieDetails, setMovieDetails] = useState(null);

  useEffect(() => {
    const abortController = new AbortController();
    setIsLoading(true);

    const fetchMovieDetails = async () => {
      try {
        const response = await axios.get(`http://www.omdbapi.com/?i=${id}&apikey=${API_KEY}`);
        console.log({ response });
        setMovieDetails(response.data);
      } catch (error) {
        setErrorMessage(error);
      }
    };
    fetchMovieDetails();
    setIsLoading(false);

    return () => abortController.abort();
  }, [id]);

  return (
    <div class="movie-details">
      {isLoading && <div className="lds-dual-ring" />}
      {!isLoading && (
        <div className="card">
          <img
            class="card-img-top"
            src={
              movieDetails?.Poster === 'N/A'
                ? 'https://placehold.it/198x264&text=Image+Not+Found'
                : movieDetails?.Poster
            }
            alt=""
          />
          <div class="card-body">
            <h5 class="card-title">{movieDetails?.Title}</h5>
            <p class="card-text">{movieDetails?.Plot}</p>
          </div>
          <ul class="list-group list-group-flush">
            <li class="list-group-item">Type: {movieDetails?.Type}</li>
            <li class="list-group-item">Year: {movieDetails?.Year}</li>
            <li class="list-group-item">Runtime: {movieDetails?.Runtime}</li>
            <li class="list-group-item">Genre: {movieDetails?.Genre}</li>
            <li class="list-group-item">Language: {movieDetails?.Language}</li>
            <li class="list-group-item">Country: {movieDetails?.Country}</li>
            <li class="list-group-item">Director: {movieDetails?.Director}</li>
            <li class="list-group-item">IMDB Rating: {movieDetails?.imdbRating}</li>
            <li class="list-group-item">Actors: {movieDetails?.Actors}</li>
            <li class="list-group-item">Awards: {movieDetails?.Awards}</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default MovieDetails;
